import React from 'react';
import '../App.css'
import {Drawer,Box,CssBaseline,IconButton, Button, AppBar, Toolbar} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import NotificationsIcon from '@mui/icons-material/Notifications';
import CampaignIcon from '@mui/icons-material/Campaign';
import ChatIcon from '@mui/icons-material/Chat';
import AssignmentIcon from '@mui/icons-material/Assignment';
import AttachFileIcon from '@mui/icons-material/AttachFile';
import CalendarMonthIcon from '@mui/icons-material/CalendarMonth';
import HandshakeIcon from '@mui/icons-material/Handshake';
import PhoneIcon from '@mui/icons-material/Phone';
import BarChartIcon from '@mui/icons-material/BarChart';
import WorkIcon from '@mui/icons-material/Work';
import PersonOffIcon from '@mui/icons-material/PersonOff';
const drawerWidth = 240;

export const Sidebar=()=>{
    const [open, setOpen] = React.useState(false);

    const handleDrawerOpen = () => {
      setOpen(true);
    };

    const handleDrawerClose = () => {
      setOpen(false);
    };
  return (
    <div>

    <Box sx={{
          display: 'flex',
          flexDirection: 'column',
          height: '100%'
        }}>
      <CssBaseline />
      <AppBar>
            <Toolbar>
                <IconButton
                aria-label="open drawer"
                onClick={handleDrawerOpen}
                edge="start">
                <MenuIcon />
                </IconButton>
            </Toolbar>
        </AppBar>
      <Drawer

        sx={{

          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
            backgroundColor:"#234872",

          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
       <div className='link'>
            <Button><NotificationsIcon />Notification</Button>
            <Button><CampaignIcon />Announcements</Button>
            <Button><ChatIcon />Chat</Button>
            <Button><AssignmentIcon />Tasks</Button>
            <Button><AttachFileIcon />Files</Button>
            <Button><CalendarMonthIcon />Calender</Button>
            <Button>Leaves<PersonOffIcon /></Button>
            <Button><WorkIcon />Career</Button>
            <Button><HandshakeIcon />Help</Button>
            <Button><PhoneIcon />Contact</Button>
            <Button><BarChartIcon />Organization chart</Button>
       </div>  
      </Drawer>

    </Box>
    </div>
  );
}
